function myMenuFunction() {
  var e = document.getElementById("myNavMenu");
  "nav-menu" === e.className
    ? (e.className += " responsive")
    : (e.className = "nav-menu");
}
function headerShadow() {
  let e = document.getElementById("header");
  document.body.scrollTop > 50 || document.documentElement.scrollTop > 50
    ? ((e.style.boxShadow = "0 1px 6px rgba(0, 0, 0, 0.1)"),
      (e.style.height = "70px"),
      (e.style.lineHeight = "70px"))
    : ((e.style.boxShadow = "none"),
      (e.style.height = "90px"),
      (e.style.lineHeight = "90px"));
}
window.onscroll = function () {
  headerShadow();
};
