const sections = document.querySelectorAll("section[id]");
function scrollActive() {
  let e = window.scrollY;
  sections.forEach((t) => {
    let i = t.offsetHeight,
      a = t.offsetTop - 50,
      n = t.getAttribute("id");
    e > a && e <= a + i
      ? document
          .querySelector(".nav-menu a[href*=" + n + "]")
          .classList.add("active-link")
      : document
          .querySelector(".nav-menu a[href*=" + n + "]")
          .classList.remove("active-link");
  });
}
